	function kapitalisasi_baru(){
		$skpd     = $this->session->userdata('kdskpd');
		$sql = "DELETE trkapitalisasi WHERE kd_skpd='$skpd'";
        $query1 = $this->db->query($sql);  
	//	$sql = "INSERT INTO trkapitalisasi SELECT kd_kegiatan,kd_rek5,nm_rek5,nilai_ubah,0,'Y',kd_skpd FROM trdrka WHERE kd_skpd='$skpd' AND kd_kegiatan in (select kd_kegiatan from trdrka where left(kd_rek5,3)='523') ORDER BY kd_kegiatan, kd_rek5";
        $sql = "INSERT INTO trkapitalisasi 
				select a.kd_kegiatan, a.kd_rek5, a.nm_rek5, a.anggaran,0,'Y', 
				a.kd_skpd, isnull(b.real_spj,0) from
				(select a.kd_skpd, a.kd_kegiatan, a.nm_kegiatan, a.kd_rek5, a.nm_rek5, 
				nilai_ubah as anggaran from trdrka a WHERE a.kd_skpd='$skpd' AND a.kd_kegiatan IN (
				SELECT kd_kegiatan FROM trdrka WHERE LEFT (kd_rek5, 3) = '523')
				group by a.kd_skpd, a.kd_kegiatan, a.nm_kegiatan, a.kd_rek5, a.nm_rek5,a.nilai_ubah) a
				left join
				(select kd_skpd, kd_kegiatan, kd_rek5, sum(real_spj) as real_spj 
				from (
				select c.kd_skpd, b.kd_kegiatan, b.kd_rek5, sum(b.nilai) as real_spj 
				from trdtransout b inner join trhtransout c on b.no_bukti=c.no_bukti and b.kd_skpd=c.kd_skpd 
				where c.kd_skpd='$skpd'
				group by c.kd_skpd, b.kd_kegiatan, b.kd_rek5
				union all
				select e.kd_skpd, d.kd_kegiatan, d.kd_rek5, sum(d.rupiah*-1) as real_spj 
				from trdkasin_pkd d inner join trhkasin_pkd e on d.no_sts=e.no_sts and d.kd_skpd=e.kd_skpd
				where e.jns_trans=5 and LEFT(d.kd_rek5,1)=5 and e.pot_khusus <>0 
				and e.kd_skpd='$skpd' group by e.kd_skpd, d.kd_kegiatan, d.kd_rek5
				union all
				select a.kd_skpd, b.kd_kegiatan, a.kd_rek5, sum(a.nilai*-1) as real_spj 
				from trdinlain a inner join TRHINLAIN b on a.no_bukti=b.no_bukti and a.kd_skpd=b.kd_skpd where pengurang_belanja=1
				and a.kd_skpd='$skpd' group by a.kd_skpd, b.kd_kegiatan, a.kd_rek5 
				) b group by kd_skpd, kd_kegiatan, kd_rek5
				) b 
				on a.kd_skpd=b.kd_skpd and a.kd_kegiatan=b.kd_kegiatan and a.kd_rek5=b.kd_rek5
				";
		$query1 = $this->db->query($sql);  
	}
	
