				select a.kd_skpd, b.kd_kegiatan, a.kd_rek5, sum(a.nilai*-1) as real_spj 
				from trdinlain a inner join TRHINLAIN b on a.no_bukti=b.no_bukti and a.kd_skpd=b.kd_skpd where pengurang_belanja=1
