function rek_potongan(){
    $lccr   = $this->input->post('q') ;
    $sql    = " SELECT kd_rek6,map_pot,nm_pot FROM ms_pot where 
                -- kd_rek6 not in ('210105010001','210105040001','210105020001','210105030001','210109010001','210106010001') and 
                ( upper(kd_rek6) like upper('%$lccr%')
                OR upper(nm_rek6) like upper('%$lccr%') )  group by kd_rek6,nm_pot,map_pot";
    $query1 = $this->db->query($sql);  
    $result = array();
    $ii     = 0;
    foreach($query1->result_array() as $resulte)
    { 
       
        $result[] = array(
                    'id' => $ii,        
                    'kd_rek6' => $resulte['kd_rek6'],  
                    'nm_rek6' => $resulte['nm_pot'],
                    'map_pot' => $resulte['map_pot'],  
                   
                    );
                    $ii++;
    }
       
    echo json_encode($result);
 $query1->free_result();       
}

