            $result[] = array(
                            'id' => $ii,        
                            'nrealisasi' => number_format($resulte['total'],2,'.',',')                                              
                        );
                        $ii++;
        }
       
           echo json_encode($result);
           $query1->free_result();  
    }
    
    
    
