        $field    = $this->input->post('field');

        $hasil=$this->db->query(" select count(*) as jumlah FROM $tabel where $field='$nomor' ");
        foreach ($hasil->result_array() as $row){
        $jumlah=$row['jumlah']; 
        }
        if($jumlah>0){
            $msg = array('pesan'=>'1');
            echo json_encode($msg);
        } else{
            $msg = array('pesan'=>'0');
            echo json_encode($msg);
        }
        
    }
    
    
