        if ($stsubah==0){
            $field="a.nilai";       
            $sts="susun";       
        }else{
            $field="a.nilai";               
            $sts="ubah";        
        }
        
        $sql = "SELECT SUM($field) as nilai FROM trdskpd a inner join trskpd b on a.kd_kegiatan=b.kd_kegiatan
                WHERE a.kd_skpd='$skpd' and a.bulan>=$awal AND a.bulan<=$ahir and a.status='$sts' and $dan ";
                
        $query1 = $this->db->query($sql);  
