function real_sumber_dana_trans() {  
        $ckdkegi  = $this->input->post('kegiatan');
        $ckdskpd  = $this->input->post('kd_skpd');
        $kd_rek6  = $this->input->post('kdrek5');
        $jenis    = $this->input->post('jenis');
        $sp2d    = $this->input->post('sp2d');
        
        if($jenis=='1'){
        $query1   = $this->db->query(" SELECT * FROM real_sumberdana('$ckdskpd','$ckdkegi','$kd_rek6')");   
        } else{
        $query1   = $this->db->query("SELECT isnull(SUM(nil_pad),0) as nil_pad,isnull(SUM(nil_dak),0) as nil_dak,
                                      SUM(nil_daknf) as nil_daknf,isnull(SUM(nil_dau),0) as nil_dau,isnull(SUM(nil_dbhp),0) as nil_dbhp,
                                      SUM(nil_did) as nil_did,SUM(nil_hpp) as nil_hpp
                    FROM trdtransout c INNER JOIN trhtransout d ON c.no_bukti=d.no_bukti AND c.kd_skpd=d.kd_skpd 
                    WHERE c.kd_kegiatan = '$ckdkegi' AND 
                    d.kd_skpd='$ckdskpd' 
                    AND c.kd_rek6='$kd_rek6' AND d.jns_spp = '$jenis' and c.no_sp2d = '$sp2d'
                    UNION ALL
                    SELECT SUM(CASE WHEN sumber='PAD' THEN nilai ELSE 0 END) as nil_pad,
                           SUM(CASE WHEN sumber='DAK FISIK' THEN nilai ELSE 0 END) as nil_dak,
                           SUM(CASE WHEN sumber='DAK NON FISIK' THEN nilai ELSE 0 END) as nil_daknf,
                           SUM(CASE WHEN sumber='DAU' THEN nilai ELSE 0 END) as nil_dau,
                           SUM(CASE WHEN sumber='DBHP' THEN nilai ELSE 0 END) as nil_dbhp,
                           SUM(CASE WHEN sumber='DID' THEN nilai ELSE 0 END) as nil_did,
                           SUM(CASE WHEN sumber='HPP' THEN nilai ELSE 0 END) as nil_hpp
                    FROM trdtransout_cmsbank c INNER JOIN trhtransout_cmsbank d ON c.no_voucher=d.no_voucher AND c.kd_skpd=d.kd_skpd 
                    WHERE c.kd_kegiatan = '$ckdkegi' AND 
                    d.kd_skpd='$ckdskpd' 
                    AND c.kd_rek6='$kd_rek6' AND d.jns_spp = '$jenis' and c.no_sp2d = '$sp2d' AND d.status_validasi='0'");   
        }
        $result   = array();
        $ii       = 0;
        foreach($query1->result_array() as $resulte)
        { 
            $result[] = array(
                        'id'             => $ii,        
