   function select_real($kegiatan='',$skpd='') {

		$sql = "select a.kd_rek6,b.nm_rek5,a.nilai,a.nilai_ubah,a.sumber from trdrka a inner join ms_rek5 b on a.kd_rek6=b.kd_rek6 where kd_kegiatan='$kegiatan' order by a.kd_rek6";                   


        $query1 = $this->db->query($sql);  
        $result = array();
        $ii = 0;
        foreach($query1->result_array() as $resulte)
        { 
				$rk=$resulte['kd_rek6'];

				$query = $this->db->query(" SELECT SUM(a.nilai) AS nilai FROM trdspp a 
											LEFT JOIN trhspp b ON a.no_spp=b.no_spp
											LEFT JOIN trhspm c ON c.no_spp=b.no_spp
											LEFT JOIN trhsp2d d ON d.no_spm=c.no_spm
											WHERE a.kd_skpd='$skpd' AND a.kd_kegiatan='$kegiatan' AND a.kd_rek6='$rk' ");  
				foreach($query->result_array() as $res){
					$sp2d=$res['nilai'];
				}

				$query = $this->db->query("SELECT SUM(a.rupiah) AS nilai FROM trdkasin_pkd a LEFT JOIN trhkasin_pkd b ON a.no_sts=b.no_sts WHERE a.kd_skpd='$skpd' AND b.kd_kegiatan='$kegiatan' AND a.kd_rek6='$rk' ");  
				foreach($query->result_array() as $res){
					$cp=$res['nilai'];
				}
				

				$result[] = array(
							'id' => $ii,        
							'kd_rek6' => $resulte['kd_rek6'],  
							'nm_rek5' => $resulte['nm_rek5'],  
							'nilai' => number_format($resulte['nilai']),
							'nilai_ubah' => number_format($resulte['nilai_ubah']),                             
							'real' => number_format($sp2d-$cp),                             
							'sisa' => number_format($resulte['nilai_ubah']-($sp2d-$cp))                             
							);
							$ii++;
        }
           
           echo json_encode($result);
    }	

