										and e.status_validasi='0'
										group by f.kd_sub_kegiatan) z
										group by z.kd_sub_kegiatan
									) g on a.kd_sub_kegiatan=g.kd_sub_kegiatan
									left join 
									(
										SELECT t.kd_sub_kegiatan, SUM(t.nilai) [penagihan] FROM trdtagih t 
