    function anggaran()
    {
        $sql = "SELECT * from tb_status_anggaran where status_aktif='1' order by id ";
        $query1 = $this->db->query($sql);
        $result = array();
        $ii = 0;
        foreach ($query1->result_array() as $resulte) {

            $result[] = array(
                'id' => $ii,
                'kode' => $resulte['kode'],
                'nama' => $resulte['nama'],
            );
            $ii++;
        }
        echo json_encode($result);
    }

